import React from "react";
import {foods} from "../data/foods";
import {captions} from "../data/captions";
import "./Foods.scss"

const Foods = () => {

    return (
        <div>
            {foods.map(food => (
                <div className="food">
                    <img className="foodpic" src={food.image}/>
                    <p className="caption">{food.caption}</p>
                </div>
                )
            )}
        </div>
    )
}

export default Foods
